package net.jeeshop.services.front.catalog;import java.util.List;import net.jeeshop.core.Services;import net.jeeshop.services.front.catalog.bean.Catalog;public interface CatalogService extends Services<Catalog> {	/**	 * 加载指定节点下的全部节点。 如果指定的节点为NULL则加载整棵树，否则加载指定对象下的所有节点	 * 	 * @param e	 * @return	 */	public List<Catalog> loadRoot(Catalog e);    /**     * 查询id 下的 名字     * @param list     * @return     */    public List<String> selectNameListByIds(List list);}