package net.jeeshop.services.front.discount.dao.impl;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.front.discount.bean.Discount;import net.jeeshop.services.front.discount.dao.DiscountDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repository("discountDaoFront")public class DiscountDaoImpl   implements DiscountDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(Discount e) {		return dao.selectPageList("front.discount.selectPageList",				"front.discount.selectPageCount", e);	}    public PagerModel selectPageList3(Discount e) {       /* dao.selectPageList("front.discount.selectPageList2",                "front.discount.selectPageCount2", e);*/        return dao.selectPageList("front.discount.selectPageList3",                "front.discount.selectPageCount3", e);    }	public List selectList(Discount e) {		return dao.selectList("front.discount.selectList", e);	}    public List getUserDiscountList(Discount e) {        return dao.selectList("front.discount.getUserDiscountList", e);    }	public Discount selectOne(Discount e) {		return (Discount) dao.selectOne("front.discount.selectOne", e);	}	public int delete(Discount e) {		return dao.delete("front.discount.deleteById", e);	}	public int update(Discount e) {		return dao.update("front.discount.update", e);	}	public int deletes(String[] ids) {		Discount e = new Discount();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(Discount e) {		return dao.insert("front.discount.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.discount.deleteById", id);	}	public Discount selectById(String id) {		return (Discount) dao.selectOne("front.discount.selectById", id);	}	public void initAllDiscount(Discount Discount) {		dao.update("front.discount.initAllDiscount",Discount);	}}