package net.jeeshop.services.manage.discount.dao.impl;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.discount.bean.Discount;import net.jeeshop.services.manage.discount.dao.DicountDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repository("dicountDaoManage")public class DicountDaoImpl implements DicountDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(Discount e) {		return dao.selectPageList("manage.discount.selectPageList",				"manage.discount.selectPageCount", e);	}	public List selectList(Discount e) {		return dao.selectList("manage.discount.selectList", e);	}	public Discount selectOne(Discount e) {		return (Discount) dao.selectOne("manage.discount.selectOne", e);	}	public int delete(Discount e) {		return dao.delete("manage.discount.deleteById", e);	}	public int update(Discount e) {		return dao.update("manage.discount.update", e);	}	public int deletes(String[] ids) {		Discount e = new Discount();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(Discount e) {		return dao.insert("manage.discount.insert", e);	}	public int deleteById(int id) {		return dao.delete("manage.discount.deleteById", id);	}	public Discount selectById(String id) {		return (Discount) dao.selectOne("manage.discount.selectById", id);	}	public void initAllDiscount(Discount Discount) {		dao.update("manage.discount.initAllDiscount",Discount);	}}