package net.jeeshop.services.manage.DiscountDetailDetail.dao.impl;import net.jeeshop.core.dao.BaseDao;import net.jeeshop.core.dao.page.PagerModel;import net.jeeshop.services.manage.discountDetail.bean.DiscountDetail;import net.jeeshop.services.manage.discountDetail.dao.DiscountDetailDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repository("dicountDetailDaoManage")public class DiscountDetailDaoImpl implements DiscountDetailDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(DiscountDetail e) {		return dao.selectPageList("manage.discountDetail.selectPageList",				"manage.discountDetail.selectPageCount", e);	}	public List selectList(DiscountDetail e) {		return dao.selectList("manage.discountDetail.selectList", e);	}	public DiscountDetail selectOne(DiscountDetail e) {		return (DiscountDetail) dao.selectOne("manage.discountDetail.selectOne", e);	}	public int delete(DiscountDetail e) {		return dao.delete("manage.discountDetail.deleteById", e);	}	public int update(DiscountDetail e) {		return dao.update("manage.discountDetail.update", e);	}	public int deletes(String[] ids) {		DiscountDetail e = new DiscountDetail();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(DiscountDetail e) {		return dao.insert("manage.discountDetail.insert", e);	}	public int deleteById(int id) {		return dao.delete("manage.discountDetail.deleteById", id);	}	public DiscountDetail selectById(String id) {		return (DiscountDetail) dao.selectOne("manage.discountDetail.selectById", id);	}	public void initAllDiscountDetail(DiscountDetail DiscountDetail) {		dao.update("manage.discountDetail.initAllDiscountDetail",DiscountDetail);	}}