mapper.select:
SELECT d.id, d.name, d.beginTime, d.endTime, d.cutType, d.cutNum,d.faceVale, case when t.orderid is not null then 4 when (#{queryDate} > d.endTime and t.status!=2) then 3 when (#{queryDate} <= d.endTime and t.status!=2) then 1 when t.status=2 then 2 else 0 end as canuse, "all" as pname, t.id as disId, t.orderId FROM t_discount_detail t JOIN t_discount d ON t.disId = d.id WHERE 1 = 1 and t.accountId=#{account} and d.proType=0 and d.proId =0
UNION SELECT d.id, d.name, d.beginTime, d.endTime, d.cutType, d.cutNum,d.faceVale, case when t.orderid is not null then 4 when (#{queryDate} > d.endTime and t.status!=2) then 3 when (#{queryDate} <= d.endTime and t.status!=2) then 1 when t.status=2 then 2 else 0 end as canuse, p.name as pname , t.id as disId, t.orderId FROM t_discount_detail t JOIN t_discount d ON t.disId = d.id left JOIN t_product p on d.proId = p.id WHERE 1 = 1 and t.accountId=#{account} and d.proType=0 and d.proId !=0
UNION SELECT d.id, d.name, d.beginTime, d.endTime, d.cutType, d.cutNum,d.faceVale, case when t.orderid is not null then 4 when (#{queryDate} > d.endTime and t.status!=2) then 3 when (#{queryDate} <= d.endTime and t.status!=2) then 1 when t.status=2 then 2 else 0 end as canuse, c.name as pname , t.id as disId, t.orderId FROM t_discount_detail t JOIN t_discount d ON t.disId = d.id left JOIN t_catalog c on d.proId = c.id WHERE 1 = 1 and t.accountId=#{account} and d.proType=1 and d.proId !=0 and c.type='p' order by endTime desc limit #{offset},#{pageSize}
